#hardware platform: FireBeetle-ESP32from machine import IISfrom machine import Pinimport timephoto = IIS(IIS.CAMERA)                     #create a iis object,and set modebutton = Pin(16, Pin.IN)photo.init()                                #init photophoto.setFramesize(IIS.HQVGA)               #set frameratephoto.setPixformat(IIS.RGB555)              #set pix formatwhile True:  if button.value() == 0:    photo.snapshot('/sd//dir1/photo1.bmp')  #show 'photo1.bmp'  time.sleep(0.5)